scripts.buildtracimlib:
NODE_ENV=production webpack -p && echo '/* eslint-disable */' | cat - dist/tracim_frontend_lib.lib.js > temp && mv temp dist/tracim_frontend_lib.lib.js && printf '
/* eslint-enable */
' >> dist/tracim_frontend_lib.lib.js && cp ./dist/tracim_frontend_lib.style.js ../frontend/dist/app/ && echo Script ended at $(date +'%H:%M:%S')

